import React from "react";
import HeroLeft from "./HeroLeft";
import HeroRight from "./HeroRight";
import catImage1 from "../Assets/hero1.png";
import catImage2 from "../Assets/cat_aktionen_header_mobile-w600-opti.webp";
import catImage3 from "../Assets/hero1.png";

export default function HeroSection() {
  const imageUrls = [catImage1, catImage2, catImage3]; // Add more image URLs as needed

  const slideshowStyle = {
    backgroundSize: "cover",
    backgroundAttachment: "fixed",
    animation: "slideshow 10s infinite",
  };

  const animationKeyframes = `
    @keyframes slideshow {
      ${imageUrls.map((_, index) => `
        ${index * (100 / imageUrls.length)}% { opacity: 1; background-image: url('${imageUrls[index]}'); }
        ${(index + 1) * (100 / imageUrls.length)}% { opacity: 0; }
      `).join('\n')}
    }
  `;

  return (
    <div className="relative" style={slideshowStyle}>
      <div className="max-w-7xl mx-auto min-h-screen">
        <div className="p-3 flex flex-wrap sm:flex-row lg:flex-row items-center sm:justify-around">
          <HeroLeft />
          <HeroRight/>
        </div>
      </div>
      <style>
        {animationKeyframes}
      </style>
    </div>
  );
}
