import React from 'react'
import { MotionConfig, motion } from "framer-motion";
import Reviews from './Reviews';

export default function HeroLeft() {
  const variants = {
    initial:{scale:0, opacity:0 },
    animate:{scale:1, opacity:1 ,transition:{
      duration:0.6
    } },
    hover:{scale:1.06}
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    border: '2px solid red',
    padding: '10px',
    borderRadius: '5px',
  };

  const inputStyle = {
    marginBottom: '10px',
    border: '2px solid red',
    borderRadius: '5px',
    padding: '5px',
  };

  const buttonStyle = {
    border: '2px solid red',
    borderRadius: '5px',
    padding: '8px 20px',
    backgroundColor: 'white',
    color: 'red',
    cursor: 'pointer',
    marginBottom: '10px',
  };

  const discountButtonStyle = {
    border: 'none',
    borderRadius: '5px',
    padding: '8px 20px',
    backgroundColor: 'red',
    color: 'white',
    cursor: 'pointer',
  };

  return (
    <MotionConfig
      transition={{ type:'spring', damping:10, stiffness:50 }}
    >
      <div className='flex flex-col items-start'>
        <motion.div layout='position' className='flex flex-col gap-6 py-5'>
          <motion.h1
            initial={{x:'-100%', opacity:0 }}
            animate={{x:0 ,opacity:1}}
            className='text-3xl font-semibold font-[Verdana,Geneva,Tahoma,sans-serif]'>Wide Option Of Choice <br />
            it's not just a food
          </motion.h1>
          <motion.h1 
            initial={{y:'100', opacity:0 }}
            animate={{y:0 ,opacity:1}}
            className='font-semibold font-[font-serif] text-5xl'>Delicious<span className='text-[#FF6600]'> Food</span></motion.h1>
          <p>A Symphony of Flavors Your Journey to Culinary Bliss Starts Now.</p>
        </motion.div>
        <motion.div layout className="self-center sm:self-start lg:self-start flex items-center sm:gap-3 lg:gap-3 gap-12 mt-5">
          <form style={formStyle}>
            <input type="text" placeholder="Zip Code" style={inputStyle} />
            <button style={buttonStyle}>Delivery</button>
            <span>or</span>
            <button style={discountButtonStyle}>15% Discount</button>
          </form>
        </motion.div>
        <Reviews/>
      </div>
    </MotionConfig>
  )
}
