import React from "react";
import HeroLeft from "./HeroLeft";
import HeroRight from "./HeroRight";
import catAktionenHeaderMobile from "../Assets/cat_aktionen_header_mobile-w600-opti.webp";

export default function HeroSection() {
  const imageUrls = [
    catAktionenHeaderMobile,
    "image2.jpg",
    // Add all your image URLs here...
    "image34.jpg",
  ];

  const slideshowStyle = {
    backgroundPosition: "center center",
    backgroundSize: "cover",
    backgroundAttachment: "fixed",
    animation: "slideshow 340s infinite", // Adjust duration according to the number of images
  };

  const animationKeyframes = `
    @keyframes slideshow {
      ${imageUrls.map((_, index) => `
        ${index * (100 / imageUrls.length)}% { opacity: 1; background-image: url('${imageUrls[index]}'); }
        ${(index + 1) * (100 / imageUrls.length)}% { opacity: 0; }
      `).join('\n')}
    }
  `;

  return (
    <div className="relative bg-yellow-50" style={slideshowStyle}>
      <div className="max-w-7xl mx-auto min-h-screen">
        <div className="p-3 flex flex-wrap sm:flex-row lg:flex-row items-center sm:justify-around">
          <HeroLeft />
          <HeroRight/>
        </div>
      </div>
      <style>
        {animationKeyframes}
      </style>
    </div>
  );
}
